import React from "react";

const WeatherCard = ({ weather }) => {
    if (!weather) return null; // Show nothing if no data is passed

    const { name, main, weather: details } = weather;
    const iconUrl = `http://openweathermap.org/img/wn/${details[0].icon}@2x.png`;

    return (
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "10px", textAlign: "center" }}>
            <h2>{name}</h2>
            <img src={iconUrl} alt={details[0].description} />
            <h3>{details[0].main}</h3>
            <p>{Math.round(main.temp)}Â°C</p>
        </div>
    );
};

export default WeatherCard;
