// src/components/WeatherCard.js
import React from 'react';

const WeatherCard = ({ weather }) => {
    const { dt_txt, main, weather: weatherDetails } = weather;
    const date = new Date(dt_txt).toLocaleDateString();
    const time = new Date(dt_txt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
    const iconUrl = `http://openweathermap.org/img/wn/${weatherDetails[0].icon}@2x.png`;

    return (
        <div className="weather-card">
            <h3>{date} {time}</h3>
            <img src={iconUrl} alt={weatherDetails[0].description} />
            <p>{weatherDetails[0].description}</p>
            <p>Temp: {main.temp}Â°C</p>
            <p>Humidity: {main.humidity}%</p>
        </div>
    );
};

export default WeatherCard;
