// src/components/WeatherModal.js
import React from 'react';
import './WeatherModal.css';

const WeatherModal = ({ weather, onClose }) => {
  if (!weather) return null;

  const {
    name,
    main: { temp, humidity, pressure },
    weather: weatherDetails,
    wind: { speed },
  } = weather;

  const iconUrl = `http://openweathermap.org/img/wn/${weatherDetails[0].icon}@2x.png`;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-button" onClick={onClose}>
          &times;
        </button>
        <h2>{name}</h2>
        <img src={iconUrl} alt={weatherDetails[0].description} />
        <p>Temperature: {Math.round(temp)}Â°C</p>
        <p>Condition: {weatherDetails[0].description}</p>
        <p>Humidity: {humidity}%</p>
        <p>Pressure: {pressure} hPa</p>
        <p>Wind Speed: {speed} m/s</p>
      </div>
    </div>
  );
};

export default WeatherModal;
