// src/components/WeatherModal.js
import React from 'react';

const WeatherModal = ({ weather, onClose }) => {
    if (!weather) return null;

    const { name, main, weather: weatherDetails, wind, isCurrent, dt_txt } = weather;
    const iconUrl = `http://openweathermap.org/img/wn/${weatherDetails[0].icon}@2x.png`;
    const dateObj = isCurrent ? new Date() : new Date(dt_txt);
    const day = isCurrent
        ? 'Today'
        : dateObj.toLocaleDateString(undefined, { weekday: 'long' });
    const date = dateObj.toLocaleDateString();

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <button onClick={onClose} className="close-button">X</button>
                <h2>{name}</h2>
                <p className="day">{day}{!isCurrent && `, ${date}`}</p>
                <img src={iconUrl} alt={weatherDetails[0].description} />
                <p className="description">{weatherDetails[0].description}</p>
                <p>Temperature: {main.temp}°C</p>
                <p>Feels Like: {main.feels_like}°C</p>
                <p>Humidity: {main.humidity}%</p>
                <p>Wind Speed: {wind.speed} m/s</p>
            </div>
        </div>
    );
};

export default WeatherModal;
