// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';
import SearchBar from './components/SearchBar';
import WeatherCard from './components/WeatherCard';
import WeatherModal from './components/WeatherModal';
import { fetchWeather, fetchForecast } from './api';

const App = () => {
    const [city, setCity] = useState('Toronto');
    const [currentWeather, setCurrentWeather] = useState(null);
    const [forecast, setForecast] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedWeather, setSelectedWeather] = useState(null);

    useEffect(() => {
        const getWeatherData = async () => {
            setLoading(true);
            setError(null);
            try {
                const weatherResponse = await fetchWeather(city);
                setCurrentWeather(weatherResponse.data);

                const forecastResponse = await fetchForecast(city);
                // Filter to get one data point per day (e.g., at 12:00 PM)
                const dailyData = forecastResponse.data.list.filter(reading => reading.dt_txt.includes("12:00:00"));
                setForecast(dailyData);
            } catch (err) {
                setError('Failed to fetch weather data. Please try again.');
            } finally {
                setLoading(false);
            }
        };

        getWeatherData();
    }, [city]);

    const handleSearch = (searchedCity) => {
        setCity(searchedCity);
    };

    const handleCardClick = (weather) => {
        setSelectedWeather(weather);
    };

    const handleCloseModal = () => {
        setSelectedWeather(null);
    };

    return (
        <div className="app">
            <h1>Weather App</h1>
            <SearchBar onSearch={handleSearch} />
            {loading && <p>Loading...</p>}
            {error && <p className="error">{error}</p>}

            <h2>5-Day Forecast</h2>
            <div className="weather-cards-container">
                {forecast.map((weather, index) => (
                    <div key={index} onClick={() => handleCardClick(weather)} className="card-wrapper">
                        <WeatherCard weather={weather} />
                    </div>
                ))}
            </div>

            {currentWeather && (
                <div 
                    className="current-weather clickable" 
                    onClick={() => handleCardClick(currentWeather)}
                >
                    <h2>{currentWeather.name}</h2>
                    <p className="day">{new Date().toLocaleDateString(undefined, { weekday: 'long' })}</p>
                    <img
                        src={`http://openweathermap.org/img/wn/${currentWeather.weather[0].icon}@2x.png`}
                        alt={currentWeather.weather[0].description}
                    />
                    <p>{currentWeather.weather[0].description}</p>
                    <p>Temperature: {currentWeather.main.temp}Â°C</p>
                    <p>Humidity: {currentWeather.main.humidity}%</p>
                </div>
            )}

            {selectedWeather && (
                <WeatherModal weather={selectedWeather} onClose={handleCloseModal} />
            )}
        </div>
    );
};

export default App;
