import React, { useState } from "react";
import CitySearch from "./components/CitySearch";
import WeatherDetails from "./components/WeatherDetails";
import Forecast from "./components/Forecast";
import "./styles/styles.css";

const App = () => {
  const [selectedCity, setSelectedCity] = useState(""); // Stores the selected city

  const handleCitySelect = (city) => {
    setSelectedCity(city); // Update the selected city
  };

  return (
    <div className="app">
      <h1>Weather App</h1>
      <CitySearch onCitySelect={handleCitySelect} />
      {/* Show current weather if a city is selected */}
      {selectedCity && <WeatherDetails city={selectedCity} />}
      {/* Show 5-day forecast if a city is selected */}
      {selectedCity && <Forecast city={selectedCity} />}
    </div>
  );
};

export default App;
